import React, { Component } from 'react';
import axios from 'axios';
import PlanesForm from './PlanesForm.js';

const SERVER_URL = 'http://localhost:3000/airplanes.json';

class Planes extends Component {
  constructor(){
    super();
    this.state = {
      airplanes: []
    }
     this.savePlane = this.savePlane.bind(this);

     const fetchPlanes = () => {
      axios.get(SERVER_URL).then((results) => {
        this.setState({airplanes: results.data});
        setTimeout(fetchPlanes, 6000);
      });
    };
    fetchPlanes();
  }

  savePlane(content) {
    axios.post(SERVER_URL, {content: content}).then((result) => {
     // Add the new secret to the existing collection of secrets in state.
     this.setState({airplanes: [...this.state.airplanes, result.data]});
   });
  }

  render() {
    return(
      <div>
      <PlanesForm onSubmit={ this.savePlane } />
      <PlanesList airplanes={this.state.airplanes}/>
      </div>
    );
  }
}

// No this in functional component
const PlanesList = (props) => {
  return (
    <div className="section">
      <div className="form">
       <h2>******* Seating Diagram Coming Soon *******</h2>
      </div>
    </div>
  );
}

export default Planes;
