import { DatePicker } from 'antd';
import moment from 'moment';

export default function year(){
const [open, setOpen] = useState(false)
  //引入useState hook函数，open初始值为flase，open为限制当前日期选择框是否开启或关闭
const [year, setYear] = useState()     //选中的year年

 function onPanelChange(date:any) {   //面板选中某年后，赋值为year，关闭面板
       yearSet(date._d.getFullYear());  //只取年值
        setOpen(false)
    }

   return (
     <div>
      Date ： <DatePicker
    		placeholder='year'   //选择框提示语
            open={open}    //当前面板的状态（打开或管壁）
            mode="year"     //当前只选择到year
            format="YYYY"     //时间显示格式
            onOpenChange={() => {setOpen(true)}}  //进行日期选择时，打开面板
            onPanelChange={onPanelChange}    //选择日期，获取选中日期值，关闭面板
            value={moment(year, 'YYYY')} />   
     </div>
          )
   }
