import React, { Component } from 'react';
import axios from 'axios';
import '../stylesheets/plane.css';

const PLANES_URL = 'http://localhost:3000/airplanes.json';

class Planes extends Component {
  constructor() {
   super();
   this.state = {
     name: []
   };


   const fetchPlanes = () => {
     axios.get(PLANES_URL).then((results) => {
       this.setState({name: results.data});
       setTimeout(fetchPlanes, 6000); // recursion alternative to setInterval
     });
   };

   fetchPlanes();
 }

 savePlanes(content) {
   axios.post(PLANES_URL, {content: content}).then((result) => {
     this.setState({name: [...this.state.name, result.data]});
   });
 }
 render() {
   return (
     <div>
       <h1>Plane Details</h1>
       <CreatePlane onSubmit={ this.savePlane } />
       <PlanesList name={ this.state.name } />
     </div>
   );
 }
}

class CreatePlane extends Component {
  constructor() {
    super();
    this.state = {content: ''};
    this._handleChange = this._handleChange.bind(this);
    this._handleSubmit = this._handleSubmit.bind(this);
  }

  _handleChange(event) {
    this.setState({content: event.target.value});
  }

  _handleSubmit(event) {
    event.preventDefault();
    this.props.onSubmit( this.state.content );
  }
  render() {
    return (
      <div className="section">
        <div className="form-planes">
          <div className="grid-col-3">
              <h1 className="title col-1"> Aeroplanes </h1>
              <button className="btn-primary col-3 h45">Create Plane</button>
           </div>
           <div>
            <form onSubmit={ this._handleSubmit } className="grid-col-3">
                <input onChange={ this._handleChange } type="text" placeholder="name" className="form-control row-1 col-1"/>
                <input type="number" placeholder="row" className="form-control row-1 col-2"/>
                <input type="number" placeholder="columns" className="form-control  row-1 col-3"/>
                <input type="submit" value="Cancel" className="btn-primary row-2 col-2"/>
                <input type="submit" value="Save" className="btn-primary row-2 col-3"/>
            </form>
           </div>
        </div>
     </div>
    );
  }
}
const PlanesList = (props) => {
  console.log('PlanesList() rendering');
  return (
    <div>
      { props.name.map( (s) => (<p key={ s.id }>{ s.content }</p>) ) }
    </div>
  );
}

export default Plane;
