import React, { Component } from 'react';
import '../stylesheets/plane.css';

class Planes extends Component {
  constructor(){
    super();
    this.state = {
      airplanes: [{"id":16,"name":"GA123","rows":4,"columns":20,"flight_id":null,"url":"http://localhost:3000/airplanes/16.json"},{"id":17,"name":"GA456","rows":5,"columns":22,"flight_id":null,"url":"http://localhost:3000/airplanes/17.json"},{"id":18,"name":"GA789","rows":6,"columns":20,"flight_id":null,"url":"http://localhost:3000/airplanes/18.json"}]
    }
  }
  render() {
    return(
      <div>
      <h1>Show Planes</h1>
      <PlanesForm/>
      <PlanesList airplanes={this.state.airplanes}/>
      </div>
    );
  }
}

  class PlanesForm extends Component {
  render() {
    return (
      <div className="section">
        <div className="form-planes">
          <div className="grid-col-3">
              <h1 className="title col-1"> Aeroplanes </h1>
              <button className="btn-primary col-3 h45">Create Plane</button>
          </div>
          <div>
            <form onSubmit={ this._handleSubmit } className="grid-col-3">
                <input onChange={ this._handleChange } type="text" placeholder="name" className="form-control row-1 col-1"/>
                <input type="number" placeholder="rows" className="form-control row-1 col-2"/>
                <input type="number" placeholder="columns" className="form-control  row-1 col-3"/>
                <input type="submit" value="Cancel" className="btn-primary row-2 col-2"/>
                <input type="submit" value="Save" className="btn-primary row-2 col-3"/>
            </form>
          </div>
         </div>
     </div>
    );
  }
}

const PlanesList = (props) => {
  return (
    <h1>Planes coming soon</h1>
  )
}

export default Planes;
